mapper.select:
select id,product_name,product_desc,product_price,product_img,product_video,
       product_banner,product_price_region,product_info
from tb_product
where product_flag = 1 and shop_id = #{form.shopId}
  and product_hot_flag == 1
  and product_cateid = #{form.cateid} 
order by id desc
limit #{form.pageNo} ,#{form.pageSize}

select p.id,product_name,product_img,pd.id as productDetailId, concat(pd.detail_name,'+',pd.detail_size,'+',pd.detail_taste) as detailType from tb_product_detail pd left join tb_product p on p.id=pd.product_id where detail_sku in
#{item}
order by p.product_extra asc
